<?php function getConnection() { $dbopts=p arse_url(getenv( 'DATABASE_URL')); $dbhost=$ dbopts[ "host"]; $dbuser=$ dbopts[ "user"]; $dbpass=$ dbopts[ "pass"]; $dbname=ltrim($dbopts[ "path"], '/'); $dbConnection=n ew PDO( "pgsql:dbname=$dbhost;dbname=$dbname", $dbuser, $dbpass); $dbConnection->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION); return $dbConnection; } ?>